#This checks if the first number is the lowest
def checkIfLow(arr, arr2):
    yes = True
    if len(arr) == 0:
        Print(arr, arr2)
        return False
    else:
        low = arr[0]
        for i in arr:
            if low > i:
                yes = False
        if yes:
            #if it is then It this puts the number into the top array
            Print(arr, arr2)
            time.sleep(1)
            arr2.append(arr[0])
            arr.pop(0)
            return True
        else:
            Print(arr, arr2)
            return True

#This just prints the arrays
def Print(arr, arr2):
    arrr2 = str(arr2)
    os.system('cls')
    print(bcolors.WARNING + arrr2 + bcolors.ENDC)
    print(arr)

#This Is what runs the functions
